import React from 'react';

const Card = (props) => {
    const { name, type } = props;
    return (
        <div className='text-center bg-light-purple dib br3 pa3 ma2 grow bw2 shadow-5'>
            <img alt={`${name}`} src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${props.id}.png?200x200`}></img>
            <div>
                <h2>{name}</h2>
                <p>{type}</p>
            </div>
        </div>
    );
}



export default Card;
