import React from 'react';

const Card = (props) => {
    return (
        <div className='text-center bg-light-purple dib br3 pa3 ma2 grow bw2 shadow-5'>
            <img alt={`${props.name}`} src={`http://pokeapi.co/media/sprites/pokemon/${props.id}.png`}></img>
            <div>
                <h2>{props.name}</h2>
                <p>{props.type}</p>
            </div>
        </div>
    );
}



export default Card;
